CREATE TABLE `db_ak`.`tbl_products` (`product_id` INT NOT NULL AUTO_INCREMENT , `product_name` VARCHAR(255) NOT NULL , `product_description` VARCHAR(255) NOT NULL , `product_category` VARCHAR(255) NOT NULL , `product_price` FLOAT NOT NULL , `in_stock` INT NOT NULL , `product_added` DATE NOT NULL , `product_image` VARCHAR(255) NOT NULL , PRIMARY KEY (`product_id`)) ENGINE = InnoDB;
